import fetch from "isomorphic-unfetch"

const Page = ({ brags }) =>
  <div>
    Brags: {brags.map((brag) => {
        return(
          <p>
            {brag.brag}
          </p>
        )
      })
    }
    <style jsx>{`
        h1,
        a {
          font-family: 'Arial';
        }

        ul {
          padding: 0;
        }

        li {
          list-style: none;
          margin: 5px 0;
        }

        a {
          text-decoration: none;
          color: blue;
        }

        a:hover {
          opacity: 0.6;
        }
      `}</style>
  </div>

Page.getInitialProps = async ({ req }) => {
  const res = await fetch('https://api.bragsheet.app')
  const json = await res.json()
  debugger
  return { brags: json.brags }
}

export default Page
